select round(sum(case when order_date = customer_pref_delivery_date then 1 else 0 end)*100.0 / count(*) ,2) as immediate_percentage from (select d.* from Delivery d join (select customer_id, min(order_date) as FirstOrder from Delivery group by customer_id)f on firstOrder = d.order_date and f.customer_id = d.customer_id)firstOrders;